import React, { useState } from "react";
import { StyleSheet, View, Image, ScrollView } from "react-native";
import { Text, RadioButton } from "react-native-paper";
import { Button, TextInput } from "../customComponents";
import InputSpinner from "react-native-input-spinner";

export default function nativeProfile({ navigation }) {
  const [name, setName] = useState({ value: "" });
  const [checked, setChecked] = useState("si");
  const [yearsLived, setYearsLived] = useState(1);

  const showSpinner = () => {
    if (checked === "no") {
      return (
        <View style={styles.spinnerContainer}>
          <Text style={styles.questions}>
            ¿Cuantos años llevas viviendo allí?
          </Text>
          <InputSpinner
            style={styles.margins}
            max={99}
            min={1}
            step={1}
            colorMax={"#00BFFF"}
            colorMin={"#00BFFF"}
            value={yearsLived}
            onChange={(yearsLived) => {
              setYearsLived(yearsLived);
            }}
          />
        </View>
      );
    }
  };
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}></View>
      <Image
        style={styles.avatar}
        source={require("../assets/womanAvatar.png")}
      />
      <View style={styles.body}>
        <View style={styles.bodyContent}>
          <Text style={styles.name}>Completa tu perfil</Text>
          <Text style={styles.info}>Cambiar imagen</Text>
          <TextInput
            color="#f0fc03"
            label="¿En que ciudad vives?"
            placeholder="Ciudad"
            style={styles.inputs}
          ></TextInput>
          <Text style={styles.questions}>¿Eres autóctono de esta ciudad?</Text>
          <View>
            <View style={styles.radioView}>
              <View style={styles.radioItem}>
                <Text style={styles.radioQuestion}>Si</Text>
                <RadioButton
                  value="yes"
                  status={checked === "yes" ? "checked" : "unchecked"}
                  onPress={() => setChecked("yes")}
                />
              </View>
              <View style={styles.radioItem}>
                <Text style={styles.radioQuestion}>No</Text>
                <RadioButton
                  value="no"
                  status={checked === "no" ? "checked" : "unchecked"}
                  onPress={() => setChecked("no")}
                />
              </View>
            </View>
          </View>
          {showSpinner()}
          <TextInput mode="flat" style={styles.inputs}></TextInput>
          <Button style={styles.button} mode="contained">
            Guardar perfil
          </Button>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  margins: {
    marginTop: 20,
  },
  spinnerContainer: {
    alignItems: "center",
  },
  inputs: {
    minWidth: 280,
    marginTop: 20,
  },
  button: {
    marginTop: 20,
    color: "grey",
  },
  header: {
    backgroundColor: "#00BFFF",
    height: 200,
  },
  container: {
    flex: 1,
    width: "100%",
    //padding:20,
    //backgroundColor: "grey",
  },
  radioView: {
    alignItems: "center",
    flexDirection: "row",
  },
  radioItem: {
    alignItems: "center",
    flexDirection: "row",
    //alignSelf: "center",
    justifyContent: "center",
    marginHorizontal: 16,
    marginTop: 16,
    // flex: 1,
  },
  avatar: {
    width: 130,
    height: 130,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
    marginBottom: 10,
    alignSelf: "center",
    position: "absolute",
    marginTop: 130,
  },
  questions: {
    marginTop: 16,
    fontSize: 16,
    color: "#696969",
  },
  radioQuestion: {
    // fontSize: 16,
    color: "#696969",
  },
  body: {
    marginTop: 40,
    alignSelf: "center",
    alignItems: "center",
    justifyContent: "center",
  },
  bodyContent: {
    //minWidth:240,
    alignItems: "center",
    padding: 30,
  },
  name: {
    fontSize: 28,
    color: "#696969",
    fontWeight: "600",
  },
  info: {
    fontSize: 16,
    color: "#00BFFF",
    marginTop: 10,
  },
});
