import React, { useState } from "react";
import { StyleSheet, View, Image, ScrollView } from "react-native";
import { Text, RadioButton } from "react-native-paper";
import { Button, TextInput } from "../customComponents";
import InputSpinner from "react-native-input-spinner";
import { Chip } from "react-native-paper";

export default function nativeProfile({ navigation }) {
  const [name, setName] = useState({ value: "" });
  const [checked, setChecked] = useState("si");
  const [yearsLived, setYearsLived] = useState(1);

  const showSpinner = () => {
    if (checked === "no") {
      return (
        <View style={styles.spinnerContainer}>
          <Text style={styles.questions}>
            ¿Cuantos años llevas viviendo ahí?
          </Text>
          <InputSpinner
            style={styles.margins}
            max={99}
            min={1}
            step={1}
            colorMax={"#00BFFF"}
            colorMin={"#00BFFF"}
            value={yearsLived}
            onChange={(yearsLived) => {
              setYearsLived(yearsLived);
            }}
          />
        </View>
      );
    }
  };
  const showChips = () => {
    return (
      <View style={styles.chipContainer}>
        <Chip style={styles.chipStyles}>Inglés</Chip>
        <Chip style={styles.chipStyles}>Italiano</Chip>
        <Chip style={styles.chipStyles}>Alemán</Chip>
        <Chip style={styles.chipStyles}>Francés</Chip>
        <Chip style={styles.chipStyles}>Español</Chip>
        <Chip style={styles.chipStyles}>Chino</Chip>
      </View>
    );
  };
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}></View>
      <Image
        style={styles.avatar}
        source={require("../assets/womanAvatar.png")}
      />
      <View style={styles.body}>
        <Text style={styles.name}>Completa tu perfil</Text>
        <Text style={styles.info}>Cambiar imagen</Text>
        <TextInput
          label="¿En que ciudad vives?"
          placeholder="Ciudad"
          style={styles.inputs}
        ></TextInput>
        <Text style={styles.questions}>¿Eres autóctono de esta ciudad?</Text>
        <View>
          <View style={styles.radioView}>
            <View style={styles.radioItem}>
              <Text style={styles.radioQuestion}>Si</Text>
              <RadioButton
                value="yes"
                status={checked === "yes" ? "checked" : "unchecked"}
                onPress={() => setChecked("yes")}
              />
            </View>
            <View style={styles.radioItem}>
              <Text style={styles.radioQuestion}>No</Text>
              <RadioButton
                value="no"
                status={checked === "no" ? "checked" : "unchecked"}
                onPress={() => setChecked("no")}
              />
            </View>
          </View>
        </View>
        {showSpinner()}
        <Text style={styles.questions}>¿Que idiomas hablas?</Text>
        {showChips()}
        <View style={styles.descriptionContainer}>
          {/* <Text style={styles.questions}>Breve descripción personal:</Text> */}
          <TextInput
            label="Breve descripción personal:"
            placeholder="¿Que es lo primero que te gustaría que los visitantes sepan de ti?"
            style={styles.inputs}
          ></TextInput>
        </View>
        <Button style={styles.buttonSave} mode="contained">
          Guardar perfil
        </Button>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  descriptionContainer: {
    width: "100%",
  },
  chipContainer: {
    alignItems: "center",
    flexDirection: "row",
    flexWrap: "wrap",
    marginTop: 20,
    justifyContent: "center",
  },
  chipStyles: {
    marginRight: 16,
    marginBottom: 10,
  },
  margins: {
    marginTop: 20,
  },
  spinnerContainer: {},
  inputs: {
    marginTop: 20,
  },
  buttonSave: {
    marginTop: 30,
    color: "grey",
  },
  header: {
    backgroundColor: "#00BFFF",
    height: 200,
  },
  container: {
    flex: 1,
    width: "100%",
  },
  radioView: {
    alignItems: "center",
    flexDirection: "row",
  },
  radioItem: {
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "center",
    marginHorizontal: 16,
    marginTop: 16,
  },
  avatar: {
    width: 130,
    height: 130,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
    marginBottom: 10,
    alignSelf: "center",
    position: "absolute",
    marginTop: 130,
  },
  questions: {
    marginTop: 16,
    fontSize: 16,
    color: "#696969",
  },
  radioQuestion: {
    // fontSize: 16,
    color: "#696969",
  },
  body: {
    width: "100%",
    marginTop: 40,
    alignItems: "center",
    //alignSelf: "center",
    justifyContent: "center",
    padding: 30,
  },
  name: {
    fontSize: 28,
    color: "#696969",
    fontWeight: "600",
  },
  info: {
    fontSize: 16,
    color: "#00BFFF",
    marginTop: 10,
  },
});
