import React, { useState, useEffect, useRef } from "react";
import {
  StyleSheet,
  View,
  Image,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import {
  Text,
  Card,
  Avatar,
  Title,
  Button,
  Paragraph,
} from "react-native-paper";
import Icon from "react-native-vector-icons/FontAwesome";
//Import AwesomeIcon from "react-native-vector-icons";
import SideMenu from "react-native-side-menu-updated";

export default function NativeHome({ navigation }) {
  const [sideOpen, setSideOpen] = useState(false);
  const isFirstRender = useRef(true);
  useEffect(() => {
    //console.log(sideOpen);
    if (isFirstRender.current) {
      isFirstRender.current = false;
    } else {
      //setSideOpen(true);
      setSideOpen(false);
    }
  }, [sideOpen]);
  const menu = (
    <View style={styles.sideMenu}>
      <Text>Probando</Text>
    </View>
  );

  return (
    <SideMenu style={styles.sideMenuContainer} isOpen={sideOpen} menu={menu}>
      <HomeContent
        style={styles.homeContent}
        navigation={navigation}
        setSideOpen={setSideOpen}
        sideOpen={sideOpen}
      />
    </SideMenu>
  );
}

function HomeContent({ navigation, setSideOpen }) {
  const LeftContent = (props) => <Avatar.Icon {...props} icon="folder" />;
  return (
    <View style={styles.container}>
      <View style={styles.scrollParent}>
        <ScrollView style={styles.scrollComponent}>
          <View style={styles.header}>
            <TouchableOpacity
              onPress={() => {
                //console.log(sideOpen);
                setSideOpen((current) => !current);
              }}
              style={styles.menuButton}
            >
              <Icon name="bars" size={40} color="#707070"></Icon>
            </TouchableOpacity>

            <View style={styles.avatarContainer}>
              <Image
                style={styles.avatar}
                source={require("../assets/womanAvatar.png")}
              />
            </View>
          </View>
          <View style={styles.envelopeViewContainer}>
            <View style={styles.envelopeContainer}>
              <TouchableOpacity onPress={() => {}} style={styles.envelopes}>
                <Icon name="envelope-o" size={30} color="#707070"></Icon>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => {}} style={styles.envelopes}>
                <Icon name="envelope-open-o" size={30} color="#707070"></Icon>
              </TouchableOpacity>
            </View>
          </View>
          <View style={styles.body}>
            <Card>
              <Card.Content>
                <Title>Card title</Title>
                <Paragraph>Card content</Paragraph>
              </Card.Content>
              <Card.Cover source={{ uri: "https://picsum.photos/700" }} />
              <Card.Actions>
                <Button>Cancel</Button>
                <Button>Ok</Button>
              </Card.Actions>
            </Card>
            <Card>
              <Card.Content>
                <Title>Card title</Title>
                <Paragraph>Card content</Paragraph>
              </Card.Content>
              <Card.Cover source={{ uri: "https://picsum.photos/700" }} />
              <Card.Actions>
                <Button>Cancel</Button>
                <Button>Ok</Button>
              </Card.Actions>
            </Card>
          </View>
        </ScrollView>
      </View>
      <View style={styles.addActivityContainer}>
        <TouchableOpacity onPress={() => {}} style={styles.addActivityButton}>
          <Icon name="plus" size={40} color="#707070"></Icon>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  scrollParent: {
    flex: 8,
  },
  homeContent: {
    //flex: 1
  },
  sideMenuContainer: {
    //flexDirection: "row",
    //flex: 1
  },
  scrollComponent: {
    //flexGrow:
    //height: 800,
    backgroundColor: "white",
    //flex: 1,
  },
  addActivityContainer: {
    //alignSelf: "flex-end",
    position: "absolute",
    //alignSelf: "flex-end",
    bottom: 0,
    // backgroundColor: "transparent",
    backgroundColor: "#BCBCBC",
    marginBottom: "10%",
    marginRight: "24%",
    width: "100%",
  },
  addActivityButton: {
    alignSelf: "flex-end",
  },
  envelopes: {
    marginLeft: 40,
  },
  envelopeViewContainer: {
    paddingTop: 4,
    borderWidth: 0.5,
    height: 40,
    justifyContent: "center",
    // height: 50,
    backgroundColor: "white",
  },
  envelopeContainer: {
    flex: 1,
    flexDirection: "row-reverse",
    marginLeft: "5%",
  },
  header: {
    flex: 1,
    justifyContent: "center",
    //flexDirection: "column",
    backgroundColor: "#00BFFF",
    height: 180,
    //width: "100%",
  },
  sideMenu: {
    paddingTop: "20%",
    paddingLeft: "10%",
    width: 250,
    //height: "100%",
    backgroundColor: "#707070",
  },
  menuButton: {
    marginLeft: "10%",
    //marginBottom: "40%",
    position: "absolute",
    alignSelf: "flex-start",
  },
  avatarContainer: {
    marginRight: "10%",
    position: "absolute",
    alignSelf: "flex-end",
  },
  avatar: {
    //marginLeft: "50%",
    width: 100,
    height: 100,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
  },
  container: {
    backgroundColor: "black",
    //flexDirection: "column",
    flex: 1,
  },
  body: {
    flex: 1,
    //padding: 30,
    //height: 800,
  },
  name: {
    fontWeight: "bold",
    fontSize: 28,
    color: "#696969",
  },
});
