import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Dimensions,
  FlatList,
  KeyboardAvoidingView,
  TextInput,
} from "react-native";
const { width, height } = Dimensions.get("window");
export default function Chat() {
  const [msg, setMsg] = useState("");
  const [messages, setMessages] = useState({
    all: [
      {
        id: 1,
        sent: true,
        msg: "Lorem ipsum dolor",
        image: "../../assets/womanAvatar.png",
      },
      {
        id: 2,
        sent: true,
        msg: "sit amet, consectetuer",
        image: "../../assets/womanAvatar.png",
      },
      {
        id: 3,
        sent: false,
        msg: "adipiscing elit. Aenean ",
        image: "../../assets/manAvatar.png",
      },
      {
        id: 4,
        sent: true,
        msg: "commodo ligula eget dolor.",
        image: "../../assets/womanAvatar.png",
      },
      {
        id: 5,
        sent: false,
        msg: "Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes",
        image: "../../assets/manAvatar.png",
      },
      {
        id: 6,
        sent: true,
        msg: "nascetur ridiculus mus. Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem. Nulla consequat massa quis enim. Donec pede justo, fringilla vel, aliquet nec, vulputate eget, arcu. In enim justo",
        image: "../../assets/womanAvatar.png",
      },
      {
        id: 7,
        sent: false,
        msg: "rhoncus ut, imperdiet",
        image: "../../assets/manAvatar.png",
      },
      {
        id: 8,
        sent: false,
        msg: "a, venenatis vitae",
        image: "../../assets/manAvatar.png",
      },
    ],
  });

  //   function reply() {
  //     var messages = this.state.messages;
  //     messages.push({
  //       id: Math.floor(Math.random() * 99999999999999999 + 1),
  //       sent: false,
  //       msg: this.state.msg,
  //       image: "https://www.bootdey.com/img/Content/avatar/avatar6.png",
  //     });
  //     this.setState({ msg: "", messages: messages });
  //   }

  //   function send() {
  //     if (this.state.msg.length > 0) {
  //       var messages = this.state.messages;
  //       messages.push({
  //         id: Math.floor(Math.random() * 99999999999999999 + 1),
  //         sent: true,
  //         msg: this.state.msg,
  //         image: "https://www.bootdey.com/img/Content/avatar/avatar1.png",
  //       });
  //       this.setState({ messages: messages });
  //       setTimeout(() => {
  //         this.reply();
  //       }, 2000);
  //     }
  //   }

  const renderItem = ({ item }) => {
    if (item.sent === false) {
      return (
        <View style={styles.eachMsg}>
          <Image source={{ uri: item.image }} style={styles.userPic} />
          <View style={styles.msgBlock}>
            <Text style={styles.msgTxt}>{item.msg}</Text>
          </View>
        </View>
      );
    } else {
      return (
        <View style={styles.rightMsg}>
          <View style={styles.rightBlock}>
            <Text style={styles.rightTxt}>{item.msg}</Text>
          </View>
          <Image source={{ uri: item.image }} style={styles.userPic} />
        </View>
      );
    }
  };
  return (
    <View style={{ flex: 1 }}>
      <KeyboardAvoidingView behavior="padding" style={styles.keyboard}>
        <FlatList
          style={styles.list}
          //extraData={this.state}
          data={messages.all}
          keyExtractor={(item) => {
            return item.id;
          }}
          renderItem={renderItem}
        />
        <View style={styles.input}>
          <TextInput
            style={{ flex: 1 }}
            value={msg}
            placeholderTextColor="#696969"
            onChangeText={(msg) => setMsg(msg)}
            blurOnSubmit={false}
            //onSubmitEditing={() => send()}
            placeholder="Type a message"
            returnKeyType="send"
          />
        </View>
      </KeyboardAvoidingView>
    </View>
  );
}

const styles = StyleSheet.create({
  keyboard: {
    flex: 1,
    justifyContent: "center",
  },
  image: {
    width,
    height,
  },
  header: {
    height: 65,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#075e54",
  },
  left: {
    flexDirection: "row",
    alignItems: "center",
  },
  right: {
    flexDirection: "row",
  },
  chatTitle: {
    color: "#fff",
    fontWeight: "600",
    margin: 10,
    fontSize: 15,
  },
  chatImage: {
    width: 30,
    height: 30,
    borderRadius: 15,
    margin: 5,
  },
  input: {
    flexDirection: "row",
    alignSelf: "flex-end",
    padding: 10,
    height: 40,
    width: width - 20,
    backgroundColor: "#fff",
    margin: 10,
    shadowColor: "#3d3d3d",
    shadowRadius: 2,
    shadowOpacity: 0.5,
    shadowOffset: {
      height: 1,
    },
    borderColor: "#696969",
    borderWidth: 1,
  },
  eachMsg: {
    flexDirection: "row",
    alignItems: "flex-end",
    margin: 5,
  },
  rightMsg: {
    flexDirection: "row",
    alignItems: "flex-end",
    margin: 5,
    alignSelf: "flex-end",
  },
  userPic: {
    height: 40,
    width: 40,
    margin: 5,
    borderRadius: 20,
    backgroundColor: "#8BBF50",
  },
  msgBlock: {
    width: 220,
    borderRadius: 5,
    backgroundColor: "#ffffff",
    padding: 10,
    shadowColor: "#3d3d3d",
    shadowRadius: 2,
    shadowOpacity: 0.5,
    shadowOffset: {
      height: 1,
    },
  },
  rightBlock: {
    width: 220,
    borderRadius: 5,
    backgroundColor: "#97c163",
    padding: 10,
    shadowColor: "#3d3d3d",
    shadowRadius: 2,
    shadowOpacity: 0.5,
    shadowOffset: {
      height: 1,
    },
  },
  msgTxt: {
    fontSize: 15,
    color: "#555",
    fontWeight: "600",
  },
  rightTxt: {
    fontSize: 15,
    color: "#202020",
    fontWeight: "600",
  },
});
