import React, { useState, useEffect, useRef } from "react";
import {
  StyleSheet,
  View,
  Image,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import {
  Text,
  Card,
  Avatar,
  Title,
  Button,
  Paragraph,
} from "react-native-paper";
import Icon from "react-native-vector-icons/FontAwesome";
//Import AwesomeIcon from "react-native-vector-icons";
import SideMenu from "react-native-side-menu-updated";

export default function NativeHome({ navigation }) {
  const [sideOpen, setSideOpen] = useState(false);
  const isFirstRender = useRef(true);
  useEffect(() => {
    //console.log(sideOpen);
    if (isFirstRender.current) {
      isFirstRender.current = false;
    } else {
      //setSideOpen(true);
      setSideOpen(false);
    }
  }, [sideOpen]);
  const menu = (
    <View style={styles.sideMenu}>
      <Text>Probando</Text>
    </View>
  );

  return (
    <SideMenu style={styles.sideMenuContainer} isOpen={sideOpen} menu={menu}>
      <HomeContent
        navigation={navigation}
        setSideOpen={setSideOpen}
        sideOpen={sideOpen}
      />
    </SideMenu>
  );
}

function HomeContent({ navigation, setSideOpen }) {
  const LeftContent = (props) => <Avatar.Icon {...props} icon="folder" />;
  return (
    <View style={styles.container}>
      <View style={styles.scrollParent}>
        <ScrollView style={styles.scrollComponent}>
          <View style={styles.header}>
            <TouchableOpacity
              onPress={() => {
                //console.log(sideOpen);
                setSideOpen((current) => !current);
              }}
              style={styles.menuButton}
            >
              <Icon name="bars" size={40} color="#707070"></Icon>
            </TouchableOpacity>

            <View style={styles.avatarContainer}>
              <Image
                style={styles.avatar}
                source={require("../assets/womanAvatar.png")}
              />
            </View>
          </View>
          <View style={styles.envelopeViewContainer}>
            <View style={styles.envelopeContainer}>
              <TouchableOpacity onPress={() => {}} style={styles.envelopes}>
                <Icon name="envelope-o" size={30} color="#707070"></Icon>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => {}} style={styles.envelopes}>
                <Icon name="envelope-open-o" size={30} color="#707070"></Icon>
              </TouchableOpacity>
            </View>
          </View>
          <View style={styles.body}>
            <View style={styles.cardComponent}>
              <View style={styles.activityImage}></View>
              <View style={styles.activityContent}></View>
            </View>
            <View style={styles.cardComponent}></View>
            <View style={styles.cardComponent}></View>
            <View style={styles.cardComponent}></View>
          </View>
        </ScrollView>
      </View>
      <View style={styles.addActivityContainer}>
        <TouchableOpacity onPress={() => {}} style={styles.addActivityButton}>
          <Icon name="plus" size={40} color="#707070"></Icon>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  activityImage: {},
  activityContent: {},
  cardComponent: {
    backgroundColor: "#BCBCBC",
    height: 200,
    marginBottom: 10,
  },
  scrollParent: {
    flex: 8,
  },
  sideMenuContainer: {},
  scrollComponent: {
    backgroundColor: "white",
  },
  body: {
    paddingTop: 10,
    paddingHorizontal: 10,
    //paddingVertical: 10,
    flex: 1,
  },
  addActivityContainer: {
    position: "absolute",
    bottom: 0,
    backgroundColor: "transparent",
    marginBottom: "10%",
    marginRight: "24%",
    width: "100%",
  },
  addActivityButton: {
    alignSelf: "flex-end",
  },
  envelopes: {
    marginLeft: 40,
  },
  envelopeViewContainer: {
    paddingTop: 4,
    borderWidth: 0.5,
    height: 40,
    justifyContent: "center",
    backgroundColor: "white",
  },
  envelopeContainer: {
    flexDirection: "row-reverse",
    marginLeft: "5%",
  },
  header: {
    justifyContent: "center",
    backgroundColor: "#00BFFF",
    height: 180,
  },
  sideMenu: {
    paddingTop: "20%",
    paddingLeft: "10%",
    width: 250,
    backgroundColor: "#707070",
  },
  menuButton: {
    marginLeft: "10%",
    position: "absolute",
    alignSelf: "flex-start",
  },
  avatarContainer: {
    marginRight: "10%",
    position: "absolute",
    alignSelf: "flex-end",
  },
  avatar: {
    width: 100,
    height: 100,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
  },
  container: {
    backgroundColor: "black",
    flex: 1,
  },
  name: {
    fontWeight: "bold",
    fontSize: 28,
    color: "#696969",
  },
});
