import React, { useState } from "react";
import { StyleSheet, View, Image, ScrollView } from "react-native";
import { Text } from "react-native-paper";
import { Button, TextInput } from "../customComponents";

export default function Profile({ navigation }) {
  const [name, setName] = useState({ value: "" });
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}></View>
      <Image
        style={styles.avatar}
        source={require("../assets/womanAvatar.png")}
      />
      <View style={styles.body}>
        <View style={styles.bodyContent}>
          <Text style={styles.name}>Completa tu perfil</Text>
          <Text style={styles.info}>Cambiar imagen</Text>
          <TextInput
            color="#f0fc03"
            label="Â¿En que ciudad vives?"
            placeholder="Ciudad"
            style={styles.inputs}
          ></TextInput>
          <TextInput mode="flat" style={styles.inputs}></TextInput>
          <Button style={styles.button} mode="contained">
            Guardar perfil
          </Button>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  inputs: {
    minWidth: 280,
    marginTop: 20,
  },
  button: {
    marginTop: 20,
    color: "grey",
  },
  header: {
    backgroundColor: "#00BFFF",
    height: 200,
  },
  container: {
    flex: 1,
    width: "100%",
    //padding:20,
    //backgroundColor: "grey",
  },
  avatar: {
    width: 130,
    height: 130,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
    marginBottom: 10,
    alignSelf: "center",
    position: "absolute",
    marginTop: 130,
  },
  name: {
    fontSize: 22,
    color: "#FFFFFF",
    fontWeight: "600",
  },
  body: {
    marginTop: 40,
    alignSelf: "center",
    alignItems: "center",
    justifyContent: "center",
  },
  bodyContent: {
    //minWidth:240,
    alignItems: "center",
    padding: 30,
  },
  name: {
    fontSize: 28,
    color: "#696969",
    fontWeight: "600",
  },
  info: {
    fontSize: 16,
    color: "#00BFFF",
    marginTop: 10,
  },
});
